.eqv BASE_ADDRESS 0x10008000
.eqv BASE_ADDRESS_REV 0x1000FFFC
.eqv KEYPRESS_ADDRESS 0xffff0000

.eqv COLOUR_PLAYER 0xff0000
.eqv COLOUR_ENEMY 0xff0000
.eqv COLOUR_HEART 0xffffff
.eqv COLOUR_BACKGROUND 0x000000
.eqv COLOUR_BARRIER 0xffffff
.eqv COLOUR_POWERUP_HEALTH 0xFFC0CB
.eqv COLOUR_POWERUP_JUMP 0x00FF00
.eqv COLOUR_POWERUP_WIN 0xFFFF00

.eqv HIGHEST_PIXEL 16380

