hearts:        .word	0 # even hearts, full heart
hearts_old:    .word	0
hearts_max:    .word    6

player_gravity: .word 0
player_oldpos:	.word 	0 # last offset
player_jumps:    .word 2
player_jumps_max:   .word 2

pickups:    .word 0 0 0 0 0 # contains offset of pickup (bottom left pixel)
total_num_pickups: .word 5

enemies:    .word 0 0 0 0 0 # contains offset from enemies (bottom left pixel)
total_num_enemies: .word 5
enemies_distance:   .word 10 # how many pixels they move before turning
enemies_max_distance:   .word 10
enemies_direction:  .word 1 # -1 for left, 1 for right

moving_platform_offset: .word 

game_over_screen:	.word 
	
