.text
.globl main
main:
	# $t0 will ALWAYS contain player location as offset from BASE_ADDRESS
	lw $t1 game_state
	li $t2 -1
	beq $t1 $t2 start_screen
	beq $t1 $zero init
	jal keypress_check
	jal apply_gravity # move player down 1 if not on top of platform or enemy.
	jal check_pickups
        jal move_enemies # move all enemies stored in array enemies
        jal render_player # updates player location
	jal update_ui
	#sleep 40ms
	li $v0 32
	li $a0 40
	syscall
	
	j main
	
	li $v0 10
	syscall

start_screen:
    jr $ra

init:
	# update game state to initialized
	li $t1 1
	sw $t1 game_state
	# load values into variables
    	li $t1 2
    	sw $t1 hearts
    	sw $t1 hearts_old
    	sw $zero player_gravity
    	sw $zero player_oldpos
    	li $t1 1
    	sw $t1 player_jumps
    	sw $t1 player_jumps_max

    	li $t1 10
    	sw $t1 enemies_distance
    	sw $t1 enemies_max_distance

    	li $t1 1
    	sw $t1 enemies_direction
    
    	# refresh screen
	jal reset_background
	
	# initialize player locations as base of sprite
	li $t0 16112
	sw $t0 player_oldpos
	
	li $a0 BASE_ADDRESS
	addi $a0 $a0 520
	lw $t1 hearts_old
	li $t2 0
	li $t3 COLOUR_HEART

init_draw_hearts_loop:
    	sw $t3 0($a0)
    	sw $t3 8($a0)
    	sw $t3 256($a0)
    	sw $t3 264($a0)
    	sw $t3 260($a0)
    	sw $t3 516($a0)

    	addi $t2 $t2 1
    	addi $a0 $a0 20 
    	bne $t2 $t1 init_draw_hearts_loop
	
	# draw player sprite
	li $t2 COLOUR_PLAYER
	add $t3 $t0 BASE_ADDRESS 

	sw $t2 0($t3)
	sw $t2 -256($t3)
	sw $t2 -512($t3)

    	# draw platforms
    	li $t1 COLOUR_BARRIER

    	# draw_platform(offset=15140, numBlocks=10)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 15140
    	sw $t4 0($sp)
    	li $t4 10
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
    	lw $ra 0($sp)
    	addi $sp $sp 4

    	# draw_platform(offset=12548, numBlocks=10)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 12548
    	sw $t4 0($sp)
    	li $t4 10
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
    	lw $ra 0($sp)
    	addi $sp $sp 4

     	# draw_platform(offset=11064, numBlocks=38)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 11064
    	sw $t4 0($sp)
    	li $t4 38
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
    	lw $ra 0($sp)
    	addi $sp $sp 4

    	# draw_platform(offset=8924, numBlocks=10)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 8924
    	sw $t4 0($sp)
    	li $t4 10
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
    	lw $ra 0($sp)
    	addi $sp $sp 4

    	# draw_platform(offset=8460, numBlocks=10)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 8460
    	sw $t4 0($sp)
    	li $t4 10
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
	lw $ra 0($sp)
    	addi $sp $sp 4

    	# draw_platform(offset=5452, numBlocks=10)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 5452
    	sw $t4 0($sp)
    	li $t4 10
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
    	lw $ra 0($sp)
    	addi $sp $sp 4

	# draw_platform(offset=3204, numBlocks=4)
    	li $a0 BASE_ADDRESS
    	addi $sp $sp -12
    	li $t4 3204
    	sw $t4 0($sp)
    	li $t4 10
    	sw $t4 4($sp)
    	sw $ra 8($sp)

    	jal draw_platform
    
    	lw $ra 0($sp)
    	addi $sp $sp 4

    	# draw base
    	li $t1 256
    	li $t2 0
    	li $t3 COLOUR_BARRIER

    	li $a0 BASE_ADDRESS
    	addi $a0 $a0 HIGHEST_PIXEL

init_draw_base:
    	sub $a1 $a0 $t1
    	sw $t3 0($a1)

    	subi $t1 $t1 4
    	bne $t1 $t2 init_draw_base

    	li $t1 256
    	li $t2 0
    	li $a0 BASE_ADDRESS 

init_draw_walls:
    	sw $t3 0($a0)
    	sw $t3 252($a0)
	
    	addi $a0 $a0 256
    	addi $t2 $t2 1
    	bne $t2 $t1 init_draw_walls
    
init_draw_powerups:
    	li $a0 BASE_ADDRESS
    	la $a1 pickups
    	li $t1 COLOUR_POWERUP_WIN
    
    	# draw one powerup
    	sw $t1 2964($a0)
    	sw $t1 2968($a0)
    	sw $t1 2708($a0)
    	sw $t1 2712($a0)

    	# save offset of powerup into array
    	li $t1 2964
    	sw $t1 0($a1)

    	# draw two powerup
    	li $t1 COLOUR_POWERUP_HEALTH
    
    	sw $t1 8216($a0)
    	sw $t1 8220($a0)
    	sw $t1 7960($a0)
    	sw $t1 7964($a0)

    	# save offset of powerup into array
    	li $t1 8216
    	sw $t1 4($a1)

    	# draw three powerup
    	li $t1 COLOUR_POWERUP_JUMP
    
    	sw $t1 8684($a0)
    	sw $t1 8688($a0)
    	sw $t1 8428($a0)
    	sw $t1 8432($a0)

    	# save offset of powerup into array
    	li $t1 8684
    	sw $t1 8($a1)

init_draw_enemies:
	li $t1 COLOUR_ENEMY
    	# draw enemy one
     	li $a0 BASE_ADDRESS
     	sw $t1 10848($a0)
     	sw $t1 10852($a0)
     	sw $t1 10592($a0)
     	sw $t1 10596($a0)

    	li $t2 10848
    	la $a0 enemies
    	sw $t2 0($a0)

    	# draw enemy one
     	li $a0 BASE_ADDRESS
     	sw $t1 16052($a0)
     	sw $t1 16056($a0)
     	sw $t1 15796($a0)
     	sw $t1 15800($a0)

    	li $t2 16052
    	la $a0 enemies
    	sw $t2 4($a0)

    	j main
    
# taking damage from an enemy makes the rendered heart on screen not +1 health. works other way around though
draw_hearts: 
	lw $t4 hearts_old
    	lw $t5 hearts
    	beq $t4 $t5 draw_hearts_end

    	li $a0 BASE_ADDRESS
    
    	addi $a0 $a0 520
    	lw $t1 hearts_max
    	li $t2 0

    	li $t3 COLOUR_BACKGROUND
    	# erase hearts
erase_hearts_loop:
    
    	sw $t3 0($a0)
    	sw $t3 8($a0)
    	sw $t3 256($a0)
    	sw $t3 264($a0)
    	sw $t3 260($a0)
    	sw $t3 516($a0)

    	addi $t2 $t2 1
    	addi $a0 $a0 20 
   	bne $t2 $t1 erase_hearts_loop

    #draw hearts
	li $a0 BASE_ADDRESS
    	addi $a0 $a0 520
    	lw $t1 hearts_old
    	li $t2 0
    	li $t3 COLOUR_HEART
    	beq $t1 $zero draw_hearts_end

draw_hearts_loop:

    	sw $t3 0($a0)
    	sw $t3 8($a0)
    	sw $t3 256($a0)
    	sw $t3 264($a0)
    	sw $t3 260($a0)
    	sw $t3 516($a0)

    	addi $t2 $t2 1
    	addi $a0 $a0 20 
    	bne $t2 $t1 draw_hearts_loop
    
draw_hearts_end:
    	lw $t1 hearts_old
    	sw $t1 hearts
    	jr $ra

move_enemies:
    	la $a0 enemies
    	# a1 has actual position of enemy
    	li $a1 BASE_ADDRESS
    	lw $t1 total_num_enemies

    	li $t2 COLOUR_ENEMY
    	li $t3 COLOUR_BACKGROUND
    	#t4 has offset of enemey

move_enemies_loop:
    	# retrieve offset
    	lw $t4 0($a0)
    	beqz $t4 move_enemies_loop_end
    	# calculate exact position
    	add $a2 $a1 $t4
    
    	# determine which direction to move enemy in
    	lw $t5 enemies_direction
    	blez $t5 move_enemies_back

move_enemies_forward:
    	sw $t3 0($a2)
    	sw $t3 -256($a2)
    	sw $t2 8($a2)
    	sw $t2 -248($a2)

    	addi $t4 $t4 4 # add 4 to
    	sw $t4 0($a0)
  
    	j check_collisions

move_enemies_back: 
    	sw $t3 4($a2)
    	sw $t3 -252($a2)
    	sw $t2 -4($a2)
    	sw $t2 -260($a2)

    	subi $t4 $t4 4
    	sw $t4 0($a0)

check_collisions:
    	addi $sp $sp -4
    	sw $ra 0($sp)
    	jal check_enemy_collisions
    	lw $ra 0($sp)
    	addi $sp $sp 4

move_enemies_loop_end:
    	addi $a0 $a0 4
    	subi $t1 $t1 1
    	bnez $t1 move_enemies_loop

move_enemies_end:
    	# update distance
    	lw $t6 enemies_distance
    	subi $t6 $t6 1
    	sw $t6 enemies_distance
   	beqz $t6 move_enemies_change_direction
    	jr $ra

move_enemies_change_direction:
    	lw $t6 enemies_direction
    	# flip sign
    	bgtz $t6 flip_to_negative

flip_to_positive:
	li $t6 1
    	j flip_signs_end
    	
flip_to_negative:
	li $t6 -1

flip_signs_end:
	sw $t6 enemies_direction
    	sw $t6 enemies_direction

    	lw $t6 enemies_max_distance
    	sw $t6 enemies_distance
    	jr $ra

check_enemy_collisions:
    	#check if t2 or it's square area == t0
    	#t4 is offset of current enemy
    	addi $t5 $t4 0
    	beq $t5 $t0 move_enemies_destroy_enemy
    	addi $t5 $t4 4
    	beq $t5 $t0 move_enemies_destroy_enemy
    	addi $t5 $t4 -256
    	beq $t5 $t0 move_enemies_destroy_enemy
    	addi $t5 $t4 -4
    	beq $t5 $t0 move_enemies_destroy_enemy
    	jr $ra

move_enemies_destroy_enemy:
    	li $t6 COLOUR_BACKGROUND
    	sw $t6 -4($a2)
    	sw $t6 -260($a2)
    	sw $t6 0($a2)
    	sw $t6 4($a2)
    	sw $t6 -256($a2)
    	sw $t6 -252($a2)
    	sw $t6 8($a2)
    	sw $t6 -248($a2)

    	li $t6 0
    	sw $t6 0($a0)

    	lw $t6 hearts_old
    	subi $t6 $t6 1
    	sw $t6 hearts_old
    	jr $ra

draw_platform:
    	lw $t4 0($sp) # offset to draw leftmost platform
    	addi $sp $sp 4

    	lw $t1 0($sp) # number of blocks to draw platform
    	addi $sp $sp 4

    	addi $a0 $t4 BASE_ADDRESS
    
    	li $t2 0
    	li $t3 COLOUR_BARRIER

draw_platform_loop:
    	sw $t3 0($a0)

    	addi $a0 $a0 4
    	addi $t2 $t2 1
    	bne $t2 $t1 draw_platform_loop

    	jr $ra

check_pickups:
    	# check if t0 is equal to any of the elements in pickups array, if so, handle the pickup
    	# technically this checks more times than it needs to, it still treats 0 as a valid offset for now
    	la $a0 pickups
    	li $a1 BASE_ADDRESS
    	lw $t1 total_num_pickups

check_pickups_loop:
    	# load pickup offset
    	lw $t3 0($a0)
    
    	# check if pickup offset is equal to player offset
    	li $t6 -1
    	beq $t3 $t0 pickup_event
    	addi $t3 $t3 4

    	li $t6 1 
    	beq $t3 $t0 pickup_event
    	addi $t3 $t3 -256
    	beq $t3 $t0 pickup_event
    	addi $t3 $t3 -4
    	li $t6 -1
    	beq $t3 $t0 pickup_event
    	j check_pickups_loop_end

pickup_event:
    	# process pickup
    	# reload address
    	lw $t3 0($a0)
    	addi $a2 $t3 BASE_ADDRESS
    	# got colour of powerup
    	blez $t6 pickup_event_alternate # if t6 < 0, the powerup was touched on the left side
    	lw $t5 0($a2)
    	j pickup_event_end

pickup_event_alternate:
   	lw $t5 4($a2)

pickup_event_end:
    	# execute effect based off what's in $t5
    	addi $sp $sp -4
    	sw $ra 0($sp)
    	jal pickup_effect
    	lw $ra 0($sp)
    	addi $sp $sp 4
	
    	# cleared powerup from screen
    	li $t4 COLOUR_BACKGROUND
    
    	sw $t4 0($a2)
    	sw $t4 4($a2)
    	sw $t4 -256($a2)
    	sw $t4 -252($a2)

check_pickups_loop_end:
   	subi $t1 $t1 1
    	addi $a0 $a0 4
    	bne $t1 $zero check_pickups_loop

    	jr $ra

pickup_effect:
    	# $t5
    	li $t2 COLOUR_POWERUP_HEALTH
    	li $t3 COLOUR_POWERUP_JUMP # add another jump
    	li $t4 COLOUR_POWERUP_WIN # add another bullet

   	beq $t5 $t2 pickup_effect_health
    	beq $t5 $t3 pickup_effect_jump
    	beq $t5 $t4 pickup_effect_win
    	j pickup_effect_end

pickup_effect_health:
    	lw $t2 hearts_old
    	addi $t2 $t2 1
    	lw $t3 hearts_max
    	bgt $t2 $t3 pickup_effect_end
    	sw $t2 hearts_old
    
    	j pickup_effect_end

pickup_effect_jump:
    	lw $t2 player_jumps_max
    	addi $t2 $t2 1
    	sw $t2 player_jumps_max
    	j pickup_effect_end

pickup_effect_win:
    	jal win_game

pickup_effect_end:
    	jr $ra

apply_gravity:
    	# this section is actually what makes the player jump. name is a misnomer
    	lw $t3 player_gravity
	li $t2 COLOUR_BARRIER
    	beq $t3 $zero apply_gravity_down
    
    	# attempt to go up, check if "up" is actually a barrier
    	addi $a0 $t0 BASE_ADDRESS
    	lw $t1 -768($a0)
    	beq $t1 $t2 apply_gravity_ceiling 
    	addi $t9 $zero -1
    	subi $t0 $t0 256
    	subi $t3 $t3 1
    	sw $t3 player_gravity
    	j apply_gravity_end

apply_gravity_ceiling:
    	# hit a ceiling
    	sw $zero player_gravity
    	j apply_gravity_end

apply_gravity_down: 
    	move $t1 $t0
    	addi $t1 $t1 BASE_ADDRESS 
    	addi $t1 $t1 256
    	#check if there's a platform below first
    	lw $t2 0($t1)
    	li $t1 COLOUR_BARRIER
    	beq $t2 $t1 apply_gravity_end_barrier
    	# if pixel below is not barrier, move down
    	addi $t0 $t0 256
    	jr $ra

apply_gravity_end_barrier:
    	# touched a platform, reset num. jumps
    	lw $t1 player_jumps_max
    	sw $t1 player_jumps
    	jr $ra
    
apply_gravity_end:
    	jr $ra

render_player:
    	lw $t1 player_oldpos
    	beq $t0 $t1 render_player_end
    	# erase old sprite
    	li $t2 COLOUR_BACKGROUND
   	addi $t1 $t1 BASE_ADDRESS
    	sw $t2 0($t1)
    	sw $t2 -256($t1)
    	sw $t2 -512($t1)

    	# draw new sprite
    	li $t2 COLOUR_PLAYER
    	addi $t1 $t0 BASE_ADDRESS
    	sw $t2 0($t1)
    	sw $t2 -256($t1)
    	sw $t2 -512($t1)

    	sw $t0 player_oldpos

render_player_end:
    	jr $ra

win_game:
    	la $a0 game_win_screen
    	la $a1 BASE_ADDRESS
    
    	li $t1 0
    	li $t2 HIGHEST_PIXEL

win_game_loop:
    	lw $t4 0($a0)
    	sw $t4 0($a1)

    	addi $a0 $a0 4
    	addi $a1 $a1 4

    	addi $t1 $t1 4
    	bne $t1 $t2 win_game_loop
    	lw $t4 0($a0)
    	sw $t4 0($a1)
    
    	li $v0 10
	syscall

game_over:
    	la $a0 game_over_screen
    	la $a1 BASE_ADDRESS
    
    	li $t1 0
    	li $t2 HIGHEST_PIXEL

game_over_loop:
    	lw $t4 0($a0)
    	sw $t4 0($a1)

    	addi $a0 $a0 4
    	addi $a1 $a1 4

    	addi $t1 $t1 4
    	bne $t1 $t2 game_over_loop
    	lw $t4 0($a0)
    	sw $t4 0($a1)
    
    	li $v0 10
	syscall

update_ui:
    	addi $sp $sp -4
    	sw $ra 0($sp)
    	jal draw_hearts
    	lw $ra 0($sp)
    	addi $sp $sp 4

    	lw $t1 hearts
    	beqz $t1 game_over
	jr $ra

reset_background:
	li $a0 BASE_ADDRESS
	li $t1 8192
	li $t2 COLOUR_BACKGROUND
	
reset_background_loop:
	sw $t2 0($a0)
	addi $a0 $a0 4
	subi $t1 $t1 1
	bnez $t1 reset_background_loop
	jr $ra

keypress_check:
	li $a0 KEYPRESS_ADDRESS
	lw $t2 0($a0)
	beq $t2 1 keypress_event
	jr $ra
	
keypress_event:
	lw $t2 4($a0)
	beq $t2 0x77 keypress_w
	beq $t2 0x61 keypress_a
	beq $t2 0x73 keypress_s
	beq $t2 0x64 keypress_d
    beq $t2 0x70 keypress_p
	jr $ra
	
keypress_p:
    	# reset everything
    	sw $zero game_state
    	jr $ra

keypress_w:
    	# check if we have double jump
    	lw $t2 player_jumps
    	beqz $t2 keypress_w_end
    	# move up
    	li $t1 12 # jump height
    	sw $t1 player_gravity
    	# decrement num. jumps
    	subi $t2 $t2 1
    	sw $t2 player_jumps

keypress_w_end:
	jr $ra

keypress_a:
    	# move left
    	addi $a0 $t0 BASE_ADDRESS
    	subi $a0 $a0 4
    	lw $t2 0($a0)
    	li $t3 COLOUR_BARRIER
    	beq $t2 $t3 keypress_a_end

    	lw $t2 -256($a0)
    	beq $t2 $t3 keypress_a_end

    	lw $t2 -512($a0)
    	beq $t2 $t3 keypress_a_end

	subi $t0 $t0 4

keypress_a_end: 
	jr $ra

keypress_s:
	jr $ra

keypress_d:
	# move right
    	addi $a0 $t0 BASE_ADDRESS
    	addi $a0 $a0 4
    	lw $t2 0($a0)
    	li $t3 COLOUR_BARRIER
    	beq $t2 $t3 keypress_d_end

    	lw $t2 -256($a0)
    	beq $t2 $t3 keypress_d_end

    	lw $t2 -512($a0)
    	beq $t2 $t3 keypress_d_end

	addi $t0 $t0 4

keypress_d_end: 
	jr $ra
